  lngTransportActivity_id AS Id, 
  CAST(1 as bit) AS Enabled, 
  datTransport_dt AS Date, 
  datTransport_dt + ' ' + datTransport_tm AS Time, 
  lngEmp_id AS StaffId, 
  CASE vchTransportMode_cd WHEN 'aTAXI Orange' THEN 1 WHEN 'bVOL' THEN 2 WHEN 'cTAXI Yellow' THEN 3 WHEN 'dAtlas' THEN 4 WHEN 'ORANGE' THEN 5 WHEN 'VOL' THEN 6 WHEN 'Cancelled' THEN 7 WHEN 'CORKYS' THEN 8 WHEN 'CUDELIVERY' THEN 9 WHEN 'zHC-CCP' THEN 10 WHEN 'HC-PP' THEN 11 WHEN 'MOMMIES' THEN 12 WHEN 'NORIDE' THEN 13 WHEN 'zORANGE' THEN 14 WHEN 'SPECCARE' THEN 15 WHEN 'zSTAFF' THEN 16 WHEN 'WECARE' THEN 17 WHEN 'TAXI' THEN 18 WHEN 'aTAXI' THEN 19 WHEN 'VAN' THEN 20 WHEN 'STAFF' THEN 21 ELSE NULL END AS ModeId, 
